import React from "react";
import classes from "./AvailableMeal.module.css";

const DUMMY_MEALS = [
  {
    id: "m1",
    name: "Sushi",
    description: "Finest fish and veggies",
    price: 22.99,
  },
  {
    id: "m2",
    name: "Schnitzel",
    description: "A german specialty!",
    price: 16.5,
  },
  {
    id: "m3",
    name: "Barbecue Burger",
    description: "American, raw, meaty",
    price: 12.99,
  },
  {
    id: "m4",
    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 18.99,
  },
];

const AvailableMeal = () => {
  return (
    <section className={classes.meals}>
      <ul>
        {DUMMY_MEALS.map((meal) => (
          <li key={meal.id}>
            <p style={{ fontSize: "18px", fontWeight: "bold" }}>{meal.name}</p>
            <p style={{ fontStyle: "italic" }}>{meal.description}</p>
            <p
              style={{
                fontSize: "18px",
                fontWeight: "bold",
                color: "brown",
              }}
            >
              {meal.price}
            </p>
            <div
              style={{ width: "100vw", height: "19px", color: "black" }}
            ></div>
          </li>
        ))}
      </ul>
    </section>
  );
};

export default AvailableMeal;
